export function getAllIndustries(): NAICSOption[] {
  return COMPLETE_NAICS_CODES;
}

export function getIndustriesWithProfiles(): NAICSOption[] {
  return COMPLETE_NAICS_CODES.filter(ind => ind.has_full_profile);
}

export function hasFullProfile(naicsCode: string): boolean {
  const industry = COMPLETE_NAICS_CODES.find(ind => ind.naics_code === naicsCode);
  return industry?.has_full_profile ?? false;
}

export function getIndustryByCode(naicsCode: string): NAICSOption | undefined {
  return COMPLETE_NAICS_CODES.find(ind => ind.naics_code === naicsCode);
}
